    public function isValidCountry(string $country) : bool {
        if(in_array($country, $this->getAllCountries())) {
            return true;
        } else {
            return false;
        }
    }

    // these functions below are mostly used for the CDN
    // split into different classes but under same namespaces?

    // https://stackoverflow.com/questions/2510434/format-bytes-to-kilobytes-megabytes-gigabytes
    // useful for expressing max file upload sizes
    public function formatBytes($bytes, $precision = 2) {
        $units = array('B', 'KB', 'MB', 'GB', 'TB');

        $bytes = max($bytes, 0);
        $pow = floor(($bytes ? log($bytes) : 0) / log(1024));
        $pow = min($pow, count($units) - 1);

        // Uncomment one of the following alternatives
        // $bytes /= pow(1024, $pow);
        // $bytes /= (1 << (10 * $pow));

        return round($bytes, $precision) . ' ' . $units[$pow];
    }

    // there has to be a better solution but this handles images so I don't really care
    function ext2mime(string $fileExtension) : string {
        if(str_starts_with($fileExtension, ".")) {
            $fileExtension = substr($fileExtension, 1);
        }

        $mimeTypes = [
            'png'  => 'image/png',
            'jpg'  => 'image/jpeg',
            'jpeg' => 'image/jpeg',
            'gif'  => 'image/gif',
            // Add more MIME types and extensions as needed
        ];

        $fileExtension = strtolower($fileExtension);

        if (array_key_exists($fileExtension, $mimeTypes)) {
            return $mimeTypes[$fileExtension];
        }

        // Return a default MIME type if the extension is not found in the array
        return 'application/octet-stream';
    }

